import os
import json
import urllib
import boto3
import datetime
from champion import generate_champion_message
from solved import generate_solved_message

USER_AGENT = os.environ['USER_AGENT']
RETRY_PROTECTION = 1  # minute
SNS_TOPIC = os.environ['SNS_TOPIC']
BOARD_URL = os.environ['BOARD_URL']
SECURITY_KEY = os.environ['SECURITY_KEY']
WEBHOOK_URL = os.environ['WEBHOOK_URL']

def load_current_data():
    request = urllib.request.Request(BOARD_URL)
    request.add_header('User-Agent', USER_AGENT)
    request.add_header('Cookie', f'session={os.environ["AOC_SESSION"]}')
    return json.loads(urllib.request.urlopen(request).read())

def get_image(place):
    match place:
        case 1:
            return 'data:image/png;base64,'
        case 2:
            return 'data:image/png;base64,'
        case 3:
            return 'data:image/png;base64,'
    return 'data:image/png;base64,'


def generate_message(year, day, member, new_data, day_part='1'):
    # get order of the star of the day
    order = 1
    after = None
    after_time = None
    when = new_data['members'][member]['completion_day_level'][day][day_part]['get_star_ts']
    earlier = []
    place = 1
    for other in new_data['members']:
        if new_data['members'][other]['local_score'] > new_data['members'][member]['local_score']:
            place += 1
        if day in new_data['members'][other]['completion_day_level']:
            if day_part in new_data['members'][other]['completion_day_level'][day]:
                if new_data['members'][other]['completion_day_level'][day][day_part]['get_star_ts'] < when:
                    order += 1
                    earlier.append((other, new_data['members'][other]['completion_day_level'][day][day_part]['get_star_ts']))

    if len(earlier) > 0:
        earlier.sort(key=lambda x: x[1])
        after, after_time = earlier[-1]
        after_time = when - after_time
        # convert to human readable time in czech
        after_time //= 60 # to minutes
        if after_time < 60:
            after_time = f"{after_time} minut"
        elif after_time < 1440:
            after_time = f"{after_time // 60} hodin a {after_time % 60} minut"
        else:
            after_time = f"více než den"

    when_iso = datetime.datetime.fromtimestamp(new_data['members'][member]['completion_day_level'][day][day_part]['get_star_ts'])
    # add 1 hour to when_iso time
    when_iso += datetime.timedelta(hours=1)

    return {'time': new_data['members'][member]['completion_day_level'][day][day_part]['get_star_ts'],
            'template': generate_solved_message,
            'data': {
                'image': get_image(order),
                'url' : BOARD_URL,
                'username': new_data['members'][member]['name'],
                'year': year,
                'day': day,
                'part': day_part,
                'when': when_iso.isoformat(),
                'order': order,
                'after': f"To je {after_time} po {new_data['members'][after]['name']}." if after is not None else "",
                'score': new_data['members'][member]['local_score'],
                'place': place
            },
            'message': f"{new_data['members'][member]['name']} vyřešil {'první' if day_part == '1' else 'druhou'} část dne {day} jako {order}. - dobrá práce!"}


def find_best(data):
    # find member in data with highest local_score
    if len(data['members']) == 0:
        return [], 0
    _max = sorted(list(set([data['members'][member]['local_score'] for member in data['members']])))[-1]
    return ', '.join(sorted([data['members'][member]['name'] for member in data['members'] if data['members'][member]['local_score'] == _max])), _max

def find_second_best(data):
    # find member in data with highest local_score
    if len(data['members']) < 2:
        return [], 0
    _max = sorted(list(set([data['members'][member]['local_score'] for member in data['members']])))[-2]
    return ','.join(sorted([data['members'][member]['name'] for member in data['members'] if data['members'][member]['local_score'] == _max])), _max


def send_notification(info):
    # SNS sending
    boto3.client('sns', region_name='us-east-1').publish(
        TopicArn=SNS_TOPIC,
        Message=info['message'],
        Subject=f'Advent Of Code',
        MessageStructure='text'
    )

    # send to webhook
    print(f'Sending ....')
    req = urllib.request.Request(WEBHOOK_URL, data = json.dumps(info['template'](info['data'])).encode('utf-8'))  # this will make the method "POST"
    req.add_header('Content-Type', 'application/json')
    print(urllib.request.urlopen(req))

def lambda_handler(event, context):
    if 'queryStringParameters' not in event or 'key' not in event['queryStringParameters'] or event['queryStringParameters']['key'] != SECURITY_KEY:
        return { 
            'statusCode' : 401
        }
    # AWS Resource and Client
    dynamodb = boto3.resource('dynamodb', region_name='us-east-1')
    table = dynamodb.Table('aoc')

    # Get the data for current year (rollover in summer)
    year = int(datetime.datetime.now().year)
    if int(datetime.datetime.now().month) < 12:
        year -= 1

    # get item for year = 2023 from dynamodb
    dbresp = table.get_item(Key={'year': year})

    # I want to query the API, parse the JSON
    # then find for each user what changed since last time
    # and then print the changes to email thread
    if 'Item' in dbresp:
        if dbresp['Item']['last_check'] > (int(datetime.datetime.now().timestamp()) - 60 * RETRY_PROTECTION):

            print('Too soon! Wait a bit!')
            # less than one minute? give back
            return {
                'message': 'Too soon! Wait a bit!',
                'statusCode': 418  # aka I'm a teapot
            }

        old_data = json.loads(dbresp['Item']['data'])
        if 'members' not in old_data:
            old_data['members'] = {}
        new_data = load_current_data()

        new_info = []
        for member in new_data['members']:
            if member not in old_data['members']:
                # any stars they gained are new stars!
                for day in new_data['members'][member]['completion_day_level']:
                    new_info.append(generate_message(year, day, member, new_data))
                    if '2' in new_data['members'][member]['completion_day_level'][day]:
                        new_info.append(generate_message(year, day, member, new_data, day_part='2'))
            else:
                if new_data['members'][member]['local_score'] > old_data['members'][member]['local_score']:
                    for day in new_data['members'][member]['completion_day_level']:
                        # compare part 1 and part 2
                        if day not in old_data['members'][member]['completion_day_level']:
                            new_info.append(generate_message(year, day, member, new_data))
                            if '2' in new_data['members'][member]['completion_day_level'][day]:
                                new_info.append(generate_message(year, day, member, new_data, day_part='2'))
                        elif '2' in new_data['members'][member]['completion_day_level'][day] and '2' not in \
                                old_data['members'][member]['completion_day_level'][day]:
                            new_info.append(generate_message(year, day, member, new_data, day_part='2'))

        # - leader change !!!
        old_best = find_best(old_data)
        new_best = find_best(new_data)
        if old_best[0] != new_best[0]:
            new_info.append(generate_leader_message(new_best,new_data))

        # sort new_info by time
        new_info = sorted(new_info, key=lambda x: x['time'])

        # print it first
        for info in new_info:
            send_notification(info)
            print(info['message'])

        print(f'Saving check from now - {int(datetime.datetime.now().timestamp())}')
        table.put_item(
            Item={'year': year, 'data': json.dumps(new_data), 'last_check': int(datetime.datetime.now().timestamp())})
    else:
        return {
            'message': f'No data in DB for {year}!',
            'statusCode': 500
        }

    return {
        'message': 'OK',
        'statusCode': 200
    }


def generate_leader_message(new_best, new_data):
    # data >> leader, old_leader, leader_points, second, second_points
    second = find_second_best(new_data)
    return {'time': 999999999999999,
            'template': generate_champion_message,
            'data': {
                'url': BOARD_URL,
                'leader': new_best[0],
                'leader_points': new_best[1],
                'second': second[0],
                'second_points': second[1]
            },
            'message': f"Nově vede {new_best[0]} s {new_best[1]} body!"}


